"use strict";
/*  Declaring the type to a const is called 'type annotation' */
// STRING
let movieTitle = "Amadeus";
movieTitle = "Arrival";
// movieTitle = 9   COMPLAINS FOR THE TYPE
//NUMBER
let numcatLives = 9;
numcatLives += 1;
// numcatLives = 'zero'  COMPLAINS FOR THE TYPE
//BOOLEAN
let gameOver = false;
gameOver = true;
// gameOver = 'true';   COMPLAINS FOR THE TYPE
